def hausdorff(array_1, array_2):

    minima = float('inf')
    hausdorff = 0

    for i in range(len(array_1)):
        for j in range(len(array_2)):
            distance_current = distance.euclidean(array_1[i], array_2[j])
            if minima > distance_current:
                minima = distance_current
        hausdorff += minima
        minima = 0

    return hausdorff/len(array_1)

